#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\Nowaki\IdeaProjects\price-comaprison-frontend-main\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/Nowaki/IdeaProjects/price-comaprison-frontend-main/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "$basedir/../../../../../vscode-languageserver@7.0.0/node_modules/vscode-languageserver/bin/installServerIntoExtension"   $args
} else {
  & "$basedir/../../../../../vscode-languageserver@7.0.0/node_modules/vscode-languageserver/bin/installServerIntoExtension"   $args
}
$env:NODE_PATH=$env_node_path
exit $LASTEXITCODE
